import React, {Component} from "react";
import axios from 'axios'


class SourceIp extends Component{
    constructor(props){
        super(props)
        this.state = {
            ips: [],
            endpoint: 10
        }
    }

    handleSubmit() {
        axios.get(`http://127.0.0.1:5000/srcip/${this.state.endpoint}`)
        .then((response)=>{
            console.log(response.data)

            this.setState({ips:response.data.srcip_details})
            // console.log(this.state)
        })
        .catch(error => {
            console.log(error)
        })
    }
    // componentDidMount(){
    //     axios.get('http://127.0.0.1:5000/srcip/10')
    //     .then((response)=>{
    //         console.log(response.data)

    //         this.setState({ips:response.data.srcip_details})
    //         // console.log(this.state)
    //     })
    //     .catch(error => {
    //         console.log(error)
    //     })
    // }
    render() {
        // const { ips } = this.state
        // console.log(ips.srcip_details)
        return (
            <div>
            <form onSubmit={this.handleSubmit}>
                <input type="number" value={this.state.endpoint} onChange={e => this.setState({endpoint: e.target.value})} />
                <input type="submit" />
            </form>
                List of IPs

                {
                    this.state.ips.length > 0 ?
                    this.state.ips.map((ip,index) => <div key={index}>{ip.connections}
                     </div>) :
                     <div>No lines captured</div>
                }
            </div>
        )
    }
}

export default SourceIp
